        <div className="category-container">
        <div className="background-image" style= /*style takes an object and can be given on any container where key is the css prtoperty which we want to odify and the value is the string url and destructuring the actual url*/
        {{backgroundImage:`url(${imageUrl})`
        }}/>
        <div className="category-body-container">
            <h2>{title}</h2>
            <p>Shop Now</p>
        </div>
        </div>
    )
}
