    products:[],
});

export const ProductsProvider = ({children}) =>{
    const [ products, setProducts ] = useState([]);
    useEffect(()=>{
        addCollectionsAndDocuments;
    },[])
    const value = {products};

    return <ProductsContext.Provider value={value}>
        {children}
    </ProductsContext.Provider>
}
