import { useState, useContext } from "react";
import { auth,createAuthUserWithEmailAndPassword, createUserDocumentFromAuth } from "../../utils/firebase/firebase.utils";

import FormInput from "../form-input/form-input.component";
import Button from "../button/button.component";

import './sign-up-form.styles.scss';

import { UserContext } from "../../contexts/user.contexts";

const defaultFormFields = {
    displayName:'',
    email:'',
    password:'',
    confirmpassword:''
}

const SignUpForm = () => {

    const [formFields,setFormFields] = useState(defaultFormFields);
    const {displayName,email,password,confirmpassword}=formFields;

    const {setCurrentUser} = useContext(UserContext);

    const resetFormFields = () =>{
        setFormFields(defaultFormFields);
    }

    const handleSubmit = async (event) => {
        event.preventDefault();

        if(password != confirmpassword){
            alert('password do not match');
            return;
        }

        try{
            const {user} = await createAuthUserWithEmailAndPassword(auth,email,password);
            setCurrentUser(user);
            await createUserDocumentFromAuth(user,{displayName});
            resetFormFields();
        }catch(error){
            if(error.code === 'auth/email-already-in-use'){
                alert('cannot create a user, email already in use');
            }else{
                console.error('User creation encountered an error', error);
            }
        }
    }

    // console.log(formFields);
    const handleChange = (event) =>{
        const {name,value}=event.target;
        setFormFields({...formFields,[name]:value});
    }
    return(
        <div className="sign-up-container">
            <h2>Don't have an account?</h2>
            <span>Sign Up with your email and password</span>
            <form onSubmit={handleSubmit}>
                <FormInput
                    label='Display Name'
                    inputOptions = {{
                        type:"text" ,
                        required : true,
                        onChange : handleChange,
                        name : "displayName",
                        value :displayName
                    }}
                    />

                <FormInput
                    label='Email'
                    inputOptions = {{
                        type:"email" ,
                        required : true,
                        onChange:handleChange,
                        name:"email",
                        value:email
                    }}
                    />

                <FormInput
                    label='Password'
                    inputOptions = {{
                        type:"password" ,
                        required : true,
                        onChange:handleChange,
                        name:"password",
                        value:password
                    }}
                    />

                <FormInput
                    label='Confirm Password'
                    inputOptions = {{
                        type:"password" ,
                        required : true,
                        onChange:handleChange,
                        name:"confirmpassword",
                        value:confirmpassword
                    }}
                    />
                <Button type='submit'>Sign Up</Button>
            </form>
        </div>
    )
}
export default SignUpForm;
