import { useState } from "react";
import { auth,createAuthUserWithEmailAndPassword, createUserDocumentFromAuth } from "../../utils/firebase/firebase.utils";
import FormInput from "../form-input/form-input.component";

const defaultFormFields = {
    displayName:'',
    email:'',
    password:'',
    confirmpassword:''
}

const SignUpForm = () => {

    const [formFields,setFormFields] = useState(defaultFormFields);
    const {displayName,email,password,confirmpassword}=formFields;

    const resetFormFields = () =>{
        setFormFields(defaultFormFields);
    }

    const handleSubmit = async (event) => {
        event.preventDefault();

        if(password != confirmpassword){
            alert('password do not match');
            return;
        }

        try{
            const {user} = await createAuthUserWithEmailAndPassword(auth,email,password);
            await createUserDocumentFromAuth(user,{displayName});
            resetFormFields();
        }catch(error){
            if(error.code === 'auth/email-already-in-use'){
                alert('cannot create a user, email already in use');
            }else{
                console.error('User creation encountered an error', error);
            }
        }
    }

    console.log(formFields);
    const handleChange = (event) =>{
        const {name,value}=event.target;
        setFormFields({...formFields,[name]:value});
    }
    return(
        <div>
            <h1>Sign Up with email and password</h1>
            <form onSubmit={handleSubmit}>
                <FormInput
                    label={displayName}
                    type="text" 
                    required 
                    onChange={handleChange}
                    name="displayName"
                    value={displayName}/>

                <FormInput
                    label={email}
                    type="email" 
                    required 
                    onChange={handleChange}
                    name="email"
                    value={email}/>

                <FormInput
                    label={displayName}
                    type="password" 
                    required 
                    onChange={handleChange}
                    name="password"
                    value={password}/>

                <FormInput
                 label={confirmpassword}
                    type="password" 
                    required 
                    onChange={handleChange}
                    name="confirmpassword"
                    value={confirmpassword}/>
                <button>Sign Up</button>
            </form>
        </div>
    )
}
export default SignUpForm;
